mapper.select:
select name
from animal_list
where eid=#{eid}

mapper.insert:
insert into animal(aid, name, character, color, other, state, weight, neutralization, age)
values aid=#{aid}, name=#{name}, character=#{character}, color=#{color}, 
		other=#{other}, state=#{state}, weight=#{weight}, neutralization=#{neutralization}, age=#{age}

mapper.update:
update animal
set name=#{name}, character=#{character}, color=#{color}, other=#{other}, 
	state=#{state}, weight=#{weight}, neutralization=#{neutralization}, age=#{age}
where aid=#{aid}
