mapper.select:
 	select *
	from(
	    select rownum as rnum,SearchSharingResult.*
	    from ( 
	    	select * 
			from sharing
			where sno is not null
							
			order by created desc
	    )  SearchSharingResult
	    
	    where rownum <= #{endRow}		 		    		    
	)		 
	where rnum >= #{startRow}

select sno, title, content, mid, created
from sharing
where sno = #{sno}

select mid
from sharing
where sno = #{sno}

mapper.update:
update sharing
set title = #{title}, content = #{content}, created = sysdate
where sno = #{sno}

update sharing
set viewcount = viewcount+1
where sno = #{sno}
