mapper.select:
 	select *
	from(
	    select rownum as rnum,SearchNoticeResult.*
	    from ( 
	    	select * 
			from notice
			where nno is not null

			order by regDate desc
	    )  SearchNoticeResult

	    where rownum <= #{endRow} < 1 then 10 else #{endRow} end	 		    		    
	)		 
	where rnum >= #{startRow} < 1 then else #{startRow} end

select nno, title, content, eid, regDate
from notice
where nno = #{nno}

select eid
from notice
where nno = #{nno}

mapper.update:
update notice
set title = #{title}, content = #{content}, upload_Date = sysdate
where nno = #{nno}

update notice
set pin = #{pin}
where nno = #{nno}

update notice
set viewcnt = viewcnt+1
where nno = #{nno}
